body {
  background: black;
  width: 100vw;
  overflow-x: hidden;
  font-family: "Helvetica-Neue", sans-serif;
  margin: 0;
}

.button {
  background: rgb(109,213,237);
  background: linear-gradient(141deg, rgba(109,213,237,1) 0%, rgba(33,147,176,1) 100%);
  padding: 1em;
  text-align: center;
  text-decoration: none;
