}

header {
  display: grid;
  margin-bottom: 3em;
  padding: 2rem;

  .logo {
    max-width: 11em;
    justify-self: center;
    grid-row: 2;
    grid-column: 1;
    z-index: 2;
  }

  .header-image {
    width: 100%;
    grid-row: 2;
    grid-column: 1;
    max-width: 44em;
    margin: 0 auto;
    margin-top: 5em; // drop below the logo guess and check size
  }

  #mm { // minimenu button
    grid-row: 1;
    grid-column: 1;
    justify-self: end;
    border: none; // browser reset
    font-size: 1em;
    z-index: 2;
  }

  form {
    display: flex;
    justify-content: center;
  }

  nav {
    display: none;

    @media (min-width: 66em) {
      display: flex;
      grid-row: 1/3;
      grid-column: 1;
      font-size: 1.953em;
      writing-mode: vertical-lr;
      justify-self: start; // keeps the click area to the text
    }

    > a {
      margin-bottom: 1em;

    }
  }
}

#about {
  background: rgb(255,255,255); // [https://cssgradient.io/]
  background: linear-gradient(121deg, rgba(255,255,255,1) 0%, rgba(109,213,237,1) 100%);
  padding: 2rem;
  display: grid;
  max-width: 66rem;
  margin: 0 auto;

  > h2 {
    background: black;
    color: #6DD5ED;
    padding: 1em;
    grid-row: 1/3;
    grid-column: 1/3;
  }

  > p, main {
    background: white;
    padding: 1em;
    grid-row: 2/4;
    grid-column: 2/4;
    margin-top: 10em; // guess and check numbers deal with the overlay

    @media (min-width: 33em) {
      margin-top: 6em;
    }

    @media (min-width: 66em) {
      margin-top: 4em;
    }
  }
}

.donate-box {
  background: rgb(109,213,237);
  background: linear-gradient(141deg, rgba(109,213,237,1) 0%, rgba(33,147,176,1) 100%);
  padding: 1rem;
  margin: 2rem;

  article {
    background: black;
    display: grid;
    grid-template-columns: 1fr 1fr;
    margin: 2em;
  }

  h3 {
    grid-column: 1;
    align-self: center;
    grid-row: 1;
    z-index: 2;
    margin: 2em;
  }

  a {
    color: transparent;
    font-size: 3em;
    -webkit-text-stroke-width: 2px;
    -webkit-text-stroke-color: #6DD5ED;
    text-decoration: none;
  }

  a:hover {
    -webkit-text-stroke-color: rgb(254,172,94);
    background: none;
  }

  .donate-image {
    grid-column: 1/3;
    // margin: 2em;
    max-width: 100%;
    grid-row: 1;
    place-self: end;
  }

  h4 {
    grid-row: 1;
    grid-column: 1/3;
    justify-self: end;
    transform: rotate(-10deg);
    color: white;
    z-index: 3;
  }
}

#pet-of-the-week {
  background: rgb(75,192,200);
  background: linear-gradient(141deg, rgba(75,192,200,1) 0%, rgba(199,121,208,1) 50%, rgba(254,172,94,1) 100%);
  padding: 2rem;
  display: grid;
  grid-template-columns: 1fr 1fr;
  margin-bottom: 1em;

  @media (min-width: 66rem) {
    margin: 2rem;
    margin-bottom: 8em;
  }

  h3 {
    grid-column: 2;
    grid-row: 1;
    text-align: center;
    align-self: center;
  }

  .potw-image {
    width: 100%;
    grid-column: 1;
    grid-row: 1;
    align-self: end;
  }

  h4 {
    grid-row: 1;
    grid-column: 1;
    place-self: end;
    margin: 1rem;
    transform: rotate(-10deg);

    a {
      color: white;
    }
  }

  a {
    color: black;
