  height: 100vh; // needs fixing to be full height or stuck to the side
  left: 100%;
  transition: all 3s;
  padding: 1em;

  > ul {
    list-style: none;
    padding-left: 0;
    font-size: 1.563em;

    > li {
      margin-bottom: 1em;
    }
  }
}

.minimenu-open {
  transform: translate3d(-100%, 0, 0);
